import React,{ useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

function Userdata()
{
    const [userData, setUserdata]= useState([]); 
    const [message,setMessage] = useState('')
    const navigate = useNavigate()
    useEffect( ()=>{
        const getUserdata= async()=>{
            const reqData= await fetch("http://localhost:5000/api/user");
            const resData= await reqData.json();
            setUserdata(resData);
           // console.log(resData);
        }
        getUserdata();
    },[]);

    const handleDelete= async (id)=>{
         let res = await fetch("http://localhost:5000/api/deleteuser/"+id,{
            method:"DELETE",
            headers:{"content-type":"application-json"}
         })

         let resjson = await res.json();
         if(resjs.status===200){
           setMessage(resjson.success)
           setTimeout(()=>{
           navigate('/userdata')
           setMessage('')
           },2000)
         }
         else{
            setMessage("Please check data")
         }
    }

    return(
        <React.Fragment>
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                    <h5 className="mt-2">User Data</h5>
                       <div className="d-grid d-md-flex justify-content-md-end mb-3">
                        <Link to="/adduser" className="btn btn-warning">Add New User</Link>
                       </div>
                       <table className="table table-bordered table-striped">
                        <thead>
                        <tr>
                        <th>Sr. No</th>
                        <th>Username</th>
                        <th>Email</th>
                        <th>Phone No</th>
                        <th>Address</th>
                        <th>Status</th>
                        <th>Action</th>
                        </tr>
                        </thead>
                        <tbody>
                         { userData.map( (userData, index)=>(                           
                        <tr key={index}>
                        <td>{index+1} </td>
                        <td>{ userData.username } </td>
                        <td>{ userData.email } </td>
                        <td>{ userData.phone } </td>
                        <td>{ userData.address } </td>
                        <td>{ userData.status===1?"Active":"Inactive" } </td>
                        <td>
                         <Link to={"/editUser/"+userData.userid} className="btn btn-success mx-2">Edit</Link>
                         <Link onClick={()=>handleDelete(userData.userid)} className="btn btn-danger">Delete</Link>
                        </td>
                        </tr>
                        )) 
                        }                        
                        </tbody>
                        </table>                            
                    </div>
                </div>
            </div>

        </React.Fragment>
    );
}

export default Userdata;
